package basic;
import java.util.*;
public class P1073 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		while(true) {
			int n=sc.nextInt();
			if(n!=0) {
				System.out.println(n);
			}
			else {
				break;
			}
		}
		
		return;
	}
}
